mapper.select:
select
    board_no, write_no, comment_id, nick_name, content, write_date, update_date 
from
    comment
where
    board_no = #{boardNo}
and write_no = #{writeNo#}    
and comment_id = #{commentId#}

mapper.delete:
delete from comment 
where
    board_no = #{boardNo} 
 and
    write_no = #{write_no}
 and
    comment_id = #{comment_id}
