mapper.select:
select count(guboon_id) as count
  from 13_t_recommendation
 where board_id = #{boardId}
   and write_no = #{writeNo}
   and guboon_id = #{enabled}
 order by recommend_date asc

mapper.delete:
delete from 
  from 13_t_board_recommand
 where board_id = #{boardId}
   and write_no = #{writeNo}
   and type_id = #{t}
   and user_id = #{userId}
