mapper.select:
select count(guboon_id) as count
  from 13_t_recommendation
 where board_id = #{boardId}
   and write_no = #{writeNo}
   and comment_id = #{commentId}
   and guboon_id = #{guboonId}
 order by recommendation_date asc

mapper.delete:
delete from 
  from 13_t_recommendation
 where board_id = #{boardId}
   and write_no = #{writeNo}
   and comment_id = #{commentId}
   and guboon_id = #{guboonId}
   and user_id = #{userId}
