mapper.select:
/* 태그 매핑 목록 조회 */
SELECT board_id
      ,write_no
      ,tag_name
  FROM 13_t_tagmapping
 WHERE board_id = #{boardId}
   AND write_no = #{writeNo}

/* 태그 매핑 통계 목록 조회 */
SELECT tag_name
      ,COUNT(tag_name) AS tagCount
  FROM 13_t_tagmapping
 WHERE board_id = #{boardId}
 GROUP BY tag_name

/* 태그 매핑 조회 */
SELECT board_id
      ,write_no
      ,tag_name
  FROM 13_t_tagmapping
 WHERE board_id = #{boardId}
   AND write_no = #{writeNo}
   AND tag_name = #{tagName}

mapper.insert:
/* 태그 매핑 저장 */
INERT INTO 13_t_tagmapping (
     board_id
    ,write_no
    ,tag_name
) VALUES (
     #{boardId}
    ,#{writeNo}
    ,#{tagName}
)

mapper.delete:
/* 태그 매핑 삭제 */
DELETE
  FROM 13_t_tagmapping
 WHERE board_id = #{boardId}
   AND write_no = #{writeNo}
