mapper.select:
		SELECT COUNT(write_no)
          FROM 13_t_techqna
         WHERE parent_id = 0
            and board_id = #{boardId}
            and category_id = #{categoryId}

		SELECT COUNT(a.write_no)
         FROM 13_t_techqna a
           JOIN 13_t_tagmapping b
              ON a.write_no = b.write_no
             AND b.board_id = '4'
             AND b.tag_name = #{tagName}
        WHERE parent_id = 0
              and board_id = #{boardId}
              and category_id = #{categoryId}

/* 목록 조회 */
SELECT a.write_no
      ,board_id
      ,category_id
      ,enabled
      ,parent_id
      ,qna_title
      ,content
      ,posting_hit
      ,write_date
      ,update_date
      ,user_id
 FROM 13_t_techqna a
   JOIN 13_t_tagmapping b
      ON a.write_no = b.write_no
     AND b.board_id = '4'
     AND b.tag_name = #{tagName}
WHERE parent_id = 0
      and board_id = #{boardId}
      and category_id = #{categoryId}
/* sort 를 기준으로 분기문을 만들어서 정렬기준을 변경해야 하나 요건이 정의되지 않아 이렇게 마무리 함. */
/* ORDER BY ${sort} desc */
LIMIT #{offset}, #{sizePerList}

mapper.insert:
/* techQna 등록 */
INSERT INTO 13_t_techqna (
     write_no
    ,board_id
    ,category_id
    ,enabled
    ,parent_id
    ,user_id
    ,qna_title
    ,content
    ,posting_hit
    ,write_date
    ,update_date
) VALUES (
     #{writeNo}
    ,#{boardId}
    ,#{categoryId}
    ,#{enabled}
    ,#{parentId}
    ,#{userId}            
    ,#{qnaTitle}
    ,#{content}
    ,0
    ,now()
    ,now()
)

mapper.update:
/* techQna 등록 */
UPDATE 13_t_techqna
   SET enabled = #{enabled}
      ,qna_title = #{qnaTitle}
      ,content   = #{content}
      ,posting_hit = #{postingHit}
      ,update_date = now()
 WHERE write_no = #{writeNo}
       and board_id = #{boardId}
       and category_id = #{categoryId}

UPDATE 13_t_techqna
   SET posting_hit = posting_hit + 1 
WHERE write_no = #{writeNo}
